
def menu
  answer = 0
  while answer < 1 || answer > 8
  puts "Bienvenue dans le menu :"
  puts "Tapez 1 pour avoir le nombre total d'élements"
  puts "Tapez 2 pour connaître le Handle le plus court"
  puts "Tapez 3 pour connaître le nombre de Handle de 5 caractères"
  puts "Tapez 4 pour connaître le nombre de Handle commençant par une majuscule"
  puts "Tapez 5 pour avoir la liste des handles dans l'ordre alphabétique"
  puts "Tapez 6 pour avoir la liste des handles dans l'ordre de longueur"
  puts "Tapez 7 pour trouver le handle @epenser, Bruce, je t'aime"
  puts "Tapez 8 pour avoir la répartition des handles selon leur longueur"
  answer = gets.chomp.to_i
  end
  return answer
end

def number_of_elements(journalists)
count = journalists.count
puts "Il y a #{count} handles."
print ">"
return
end

def shortest_handle(journalists)
  short = journalists.min_by {|journalists| journalists.size}
  puts "Le Handle le plus court est #{short}."
  print ">"
return
end

 def number_of_five(journalists)
   five1 = journalists.select{|journalists| journalists.size == 6}
   five = five1.count
   puts "Il y a #{five} handles de 5 caractères."
   print ">"
   return
 end

 def start_with_caps(journalists)
   journalists1 = journalists.tr("@", "")
   capss = journalists1.select{|journalists1| journalists1.start_with? "A"}
   caps = capss.count
   puts "Il y a #{caps} handles qui commencent par une majuscule."
   print ">"
   return
 end

def sort_by_alpha(journalists)
  journalistsalpha = journalists.sort
  puts "Voici la liste des handles triée par ordre alphabétique #{journalistsalpha}."
  print ">"
  return
end

def sort_by_lenght(journalists)
  journalistslength = journalists.sort_by(&:length)
  puts "Voici la liste des handles triée par ordre alphabétique #{journalistslength}."
  print ">"
  return
end

def find_bruce(journalists)
  findpenser = journalists.index("@epenser")
  puts "Voici la position du handle @epenser est : #{findpenser}."
  print ">"
  return
end

def repart(journalists)
  minword = journalists.min_by {|journalists| journalists.size}
  maxword = journalists.max_by {|journalists| journalists.size}

  min = minword.size
  max = maxword.size

  while min <= max
    handlesize = journalists.select{|journalists| journalists.size == min}
    handlesizecount = handlesize.count
    if handlesizecount > 0
    puts "Il y a #{handlesizecount} handle(s) de longueur #{min - 1}."

    end
  min += 1
  end
return
end


